import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Card } from 'react-bootstrap'
import { createFinance, getCustomerInfo,isAuthenticated } from './fetchvehicles';
//get customer info instade if catagorey and replace it 
const AddFinance = () => {
    const [values, setValues] = useState({
        occupation: '',
        income: '',
        ssn: '',
        customer: [],
        dob: '',
       // photo: '',
        employersname: '',
        credit: '',
        createdFinance: '',
        status: '',
        paymentAmount: '',
        interstRate: '',
        loading: false,
        error: '',
        redirectToProfile: false,
        formData: ''
    });
   // const userObj = JSON.parse(localStorage.getItem('UserAuth'));
    //const userid = userObj.customer._id;
    const {customer,  token } = isAuthenticated();
    const {
        occupation,
        income,
        ssn,
        dob,
        employersname,
        credit,
        status,
        paymentAmount,
        interstRate,
        loading,
        error,
        createdFinance,
        redirectToProfile,
        formData
    } = values;

    // load categories and set form data
    // onChange={handleChange('customer')} className="form-control" value={customer._id}>
    const init = () => {
        getCustomerInfo().then(data => {
            if (data.error) {
                setValues({ ...values, error: data.error });
            } else {
                setValues({
                    ...values,
                    customer: data,
                    formData: new FormData()
                });
            }
            console.log(customer);
        });
    };

    useEffect(() => {
        init();
    }, []);
    //name === 'photo' ? event.target.files[0] :
    const handleChange = name => event => {
        const value =  event.target.value;
        formData.set(name, value);
        setValues({ ...values, [name]: value });
    };

    const clickSubmit = event => {
        event.preventDefault();
        setValues({ ...values, error: '', loading: true });

        createFinance(customer._id, token, formData).then(data => {
            if (data.error) {
                setValues({ ...values, error: data.error });
            } else {
                setValues({
                    ...values,
                    occupation: '',
                    income: '',
                    interstRate: '',
                    ssn: '',
                    employersname: '',
                    photo: '',
                    loading: false,
                });
            }
        });
    };

    const newPostForm = () => (
        <form className="mb-3" onSubmit={clickSubmit}>
            

            <div className="form-group">
                <label className="text-muted">occupation</label>
                <input onChange={handleChange('occupation')} type="text" className="form-control" value={occupation} />
            </div>

            <div className="form-group">
                <label className="text-muted">income</label>
                <textarea onChange={handleChange('income')} type="text" className="form-control" value={income} />
            </div>

            <div className="form-group">
                <label className="text-muted">ssn</label>
                <input onChange={handleChange('ssn')} type="text" className="form-control" value={ssn} />
            </div>


            <div className="form-group">
                <label className="text-muted">dob</label>
                <input onChange={handleChange('dob')} type="text" className="form-control" value={dob}/>
            </div>

            <div className="form-group">
                <label className="text-muted">employersname</label>
                <input onChange={handleChange('employersname')} type="text" className="form-control" value={employersname} />
            </div>
            <div className="form-group">
                <label className="text-muted">credit</label>
                <input onChange={handleChange('credit')} type="text" className="form-control" value={credit} />
            </div>
            <div className="form-group">
                <label className="text-muted">status</label>
                <input onChange={handleChange('status')} type="text" className="form-control" value={status} />
            </div>
            <div className="form-group">
                <label className="text-muted">paymentAmount</label>
                <input onChange={handleChange('paymentAmount')} type="text" className="form-control" value={paymentAmount} />
            </div>
            <div className="form-group">
                <label className="text-muted">interstRate</label>
                <input onChange={handleChange('interstRate')} type="text" className="form-control" value={interstRate} />
            </div>
            <button className="btn btn-outline-primary">Submit Finance-Form</button>
        </form>
    );

    const showError = () => (
        <div className="alert alert-danger" style={{ display: error ? '' : 'none' }}>
            {error}
        </div>
    );

    const showSuccess = () => (
        <div className="alert alert-info" style={{ display: createdFinance ? '' : 'none' }}>
            <h2>{`${createdFinance}`} Your Finance Form is Submited!</h2>
        </div>
    );

    const showLoading = () =>
        loading && (
            <div className="alert alert-success">
                <h2>Loading...</h2>
            </div>
        );

    return (
        <Card> 
            
            <div className="row">
                <div className="col-md-8 offset-md-2">
                    {showLoading()}
                    {showSuccess()}
                   
                    {newPostForm()}
                </div>
            </div>
        </Card>
    );
};
// {showError()}
export default AddFinance;
