import  axios  from 'axios';
import queryString from "query-string";
import React, { useState, useEffect } from 'react';
import '../Styles/editAccount_style.css'
import Footer from './footer'


const getVehicles = (e) => {
    //Here we will sign up the user then log them in
    //e.preventDefault();
    axios.get('http://localhost:8000/api/vehicles/findall') 
    .then(response => {
        return response.json();
        //console.log(response.data);
    })
    .catch( err => console.log("Axios request failed", err));
};

const BrowseInventory = () => {

    const [allVehicles,setallVehicles] = useState ([]);
    const [productsByArrival, setProductsByArrival] = useState([]);

    const [error, setError] = useState(false);

    
    const loadallVehicles = () => {
        getVehicles().then(data => {
            if (data.error) {
                setError(data.error);
            } else {
                setallVehicles(data);
            }
        });
    };

    const loadProductsByArrival = () => {
        getProducts('createdAt').then(data => {
            console.log(data);
            if (data.error) {
                setError(data.error);
            } else {
                setProductsByArrival(data);
            }
        });
    };

    useEffect(() => {
        loadallVehicles();
        loadProductsByArrival();
    }, []);

    return ( 
        <div>
                <div>  
                    <h2> New Arrivals cars </h2> 
                {    
                allVehicles.map((product, i) => (
                            <div key={i} >
                            < Footer Product = {product} />
                            </div>
                        ))}
                </div>

                <div>  
                    <h2> New Arrivals cars </h2> 
                {    
                productsByArrival.map((product, i) => (
                            <div key={i} >
                            < Footer Product = {product} />
                            </div>
                        ))}
                </div>
                
        </div>
    )
}

export default BrowseInventory;
