import React, { useState, useEffect } from 'react';
import Layout from '../core/Layout';
import { isAuthenticated } from '../auth';
import { Link } from 'react-router-dom';
import { Card } from 'react-bootstrap'
import { createFinance, getCustomerInfo } from './fetchvehicles';
//get customer info instade if catagorey and replace it 
const AddFinance = () => {
    const [values, setValues] = useState({
        occupation: '',
        income: '',
        ssn: '',
        customer: [],
        dob: '',
        photo: '',
        employersname: '',
        credit: '',
        status: '',
        paymentAmount: '',
        interstRate: '',
        loading: false,
        error: '',
        redirectToProfile: false,
        formData: ''
    });

    const { customer, token } = isAuthenticated();
    const {
        occupation,
        income,
        ssn,
        dob,
        employersname,
        credit,
        status,
        paymentAmount,
        interstRate,
        loading,
        error,
        redirectToProfile,
        formData
    } = values;

    // load categories and set form data
    const init = () => {
        getCustomerInfo().then(data => {
            if (data.error) {
                setValues({ ...values, error: data.error });
            } else {
                setValues({
                    ...values,
                    customer: data,
                    formData: new FormData()
                });
            }
        });
    };

    useEffect(() => {
        init();
    }, []);

    const handleChange = name => event => {
        const value = name === 'photo' ? event.target.files[0] : event.target.value;
        formData.set(name, value);
        setValues({ ...values, [name]: value });
    };

    const clickSubmit = event => {
        event.preventDefault();
        setValues({ ...values, error: '', loading: true });

        createFinance(customer._id, token, formData).then(data => {
            if (data.error) {
                setValues({ ...values, error: data.error });
            } else {
                setValues({
                    ...values,
                    occupation: '',
                    income: '',
                    interstRate: '',
                    ssn: '',
                    employersname: '',
                    photo: '',
                    loading: false,
                });
            }
        });
    };

    const newPostForm = () => (
        <form className="mb-3" onSubmit={clickSubmit}>
            <h4>Post Photo</h4>
            <div className="form-group">
                <label className="btn btn-secondary">
                    <input onChange={handleChange('photo')} type="file" name="photo" accept="image/*" />
                </label>
            </div>

            <div className="form-group">
                <label className="text-muted">occupation</label>
                <input onChange={handleChange('occupation')} type="text" className="form-control" value={occupation} />
            </div>

            <div className="form-group">
                <label className="text-muted">income</label>
                <textarea onChange={handleChange('income')} className="form-control" value={income} />
            </div>

            <div className="form-group">
                <label className="text-muted">ssn</label>
                <input onChange={handleChange('ssn')} type="number" className="form-control" value={ssn} />
            </div>

            <div className="form-group">
                <label className="text-muted">customer</label>
                <select onChange={handleChange('customer')} className="form-control">
                    <option>Please select</option>
                    {customer &&
                        customer.map((c, i) => (
                            <option key={i} value={c._id}>
                                {c.email}
                            </option>
                        ))}
                </select>
            </div>

            <div className="form-group">
                <label className="text-muted">dob</label>
                <select onChange={handleChange('dob')} className="form-control">
                </select>
            </div>

            <div className="form-group">
                <label className="text-muted">employersname</label>
                <input onChange={handleChange('employersname')} type="number" className="form-control" value={quantity} />
            </div>
            <div className="form-group">
                <label className="text-muted">credit</label>
                <input onChange={handleChange('credit')} type="number" className="form-control" value={quantity} />
            </div>
            <div className="form-group">
                <label className="text-muted">status</label>
                <input onChange={handleChange('status')} type="number" className="form-control" value={quantity} />
            </div>
            <div className="form-group">
                <label className="text-muted">paymentAmount</label>
                <input onChange={handleChange('paymentAmount')} type="number" className="form-control" value={quantity} />
            </div>
            <div className="form-group">
                <label className="text-muted">interstRate</label>
                <input onChange={handleChange('interstRate')} type="number" className="form-control" value={quantity} />
            </div>
            <button className="btn btn-outline-primary">Submit Finance-Form</button>
        </form>
    );

    const showError = () => (
        <div className="alert alert-danger" style={{ display: error ? '' : 'none' }}>
            {error}
        </div>
    );

    const showSuccess = () => (
        <div className="alert alert-info" style={{ display: createdFinance ? '' : 'none' }}>
            <h2>{`${createdFinance}`} Your Finance Form is Submited!</h2>
        </div>
    );

    const showLoading = () =>
        loading && (
            <div className="alert alert-success">
                <h2>Loading...</h2>
            </div>
        );

    return (
        <CARD title="Create finace application">
            <div className="row">
                <div className="col-md-8 offset-md-2">
                    {showLoading()}
                    {showSuccess()}
                    {showError()}
                    {newPostForm()}
                </div>
            </div>
        </CARD>
    );
};

export default AddFinance;
