import React, { useState, useEffect } from 'react';
import axios from 'axios';
const AccountInformation = () => {
    const [customer, setCustomer] = useState([])
    const [stopLoop, setStopLoop] = useState(0);

    useEffect( () => {
        const userObj = JSON.parse(localStorage.getItem('UserAuth'));
        const userid = userObj.customer._id

        axios.get(`http://localhost:8000/api/user/${userid}`, {
            headers: {
                'Authorization': `Bearer ${userObj.token}`
            }
        }).then((res) =>{
            setCustomer(obj => [...obj, res.data.customer])
            console.log(customer)
        })
        .catch((err) => console.log(err))
    })
    return (
        <div>
           <h1><strong>Customer Information</strong></h1>
           <hr></hr>
           <p>Name: {`${customer.name}`} </p>
           <p>Email: {customer.email} </p>
           <p>Vehicle(s): {customer.buyHistory} </p>
           <p>Credit Card: #: {customer.CreditCardNo} </p>
           <p>CVV: {customer.CVV} </p>
           <p>Home Address: {customer.Street_Address} </p>
           <p>Auto Insurer: Nationwide </p>
           <p>Zip code: {customer.Zip} </p>
           <p>Last 4 SSN #: 3232 </p>
        </div>
    )
}

export default AccountInformation;
